mapper.select:
select count(*)
from board b
    join b_category c on c.category_no = b.category_no
    join member m on m.member_no = b.member_no
    join area a on b.area_no = a.area_no
where b.board_status = 'Y'
    and m.member_category = #{category}

    and b.category_no = #{categoryNo}


    and a.area_no IN 
    
        #{area}

select b.board_No, c.category_Name, b.board_Title, m.member_NickName, a.area_Name, b.board_create_date, b.board_count, m.member_category , b.category_no from board b join b_category c on(c.category_no=b.category_no) join member m on(m.member_no=b.member_no) join area a on(b.area_no=a.area_no) where b.board_status='Y' and (m.member_category='A' and b.category_no = 0) or m.member_category=#{category}
and b.category_no = #{categoryNo}
and a.area_no IN
#{area}
order by board_no desc

mapper.update:
update board
set board_count = board_count+1
where board_no=#{bId}

update board
set board_title = #{boardTitle}, board_content = #{boardContent}, board_update_date=sysdate, category_no=#{categoryNo}, area_no=#{areaNo}
where board_no = #{boardNo}

update board
set board_status ='N'
where board_no=#{bId}

update reply
set reply_content = #{replyContent}
where reply_no = #{replyNo}

update reply
set reply_status = 'N'
where reply_no=#{rId}

mapper.select:
select count(*)
from board_like
where board_no = #{boardNo}

select count(*)
from reply_like
where reply_no = #{rId}

SELECT * 
FROM ATTACHMENT
WHERE ATTM_STATUS = 'Y' AND REF_BOARD_NO IN #{item.boardNo}

select count(*)
from board
where category_no = 99 and board_status = 'Y' and member_no = #{memberNo}
