mapper.update:
UPDATE chat_room_message
SET read_count = read_count - 1
WHERE chat_room_no = #{chatRoomNo}
  AND member_no != #{memberNo}
  AND read_count > 0

UPDATE chat_room_message
SET read_count = read_count - 1
WHERE chat_room_no = #{chatRoomNo}
  AND member_no != #{memberNo}
  AND read_count > 0
RETURNING chat_massage_no

