mapper.select:
select *
from patient
where member_no = #{memberNo}

select *
from hospital
where hospital_name = #{hospitalName} and hospital_address = #{hospitalAddress}

select pt_no
from patient
where member_no = #{memberNo}


select mat_date
from matching_date
where mat_no = #{matNo}

select count(*)
from mat_pt_info
where mat_no = #{matNo}

select mat_no
from mat_pt_info
where pt_no = #{ptNo}

mapper.delete:
DELETE FROM member_info
WHERE member_no = #{memberNo}
AND (
    category_no IN

        #{symptom}


        OR category_no IN

            #{selectedDiseaseLevel}



        OR category_no = #{mobility}


        OR category_no = #{service}

)

mapper.select:
    SELECT DISTINCT ic.category_no
	FROM info_category ic
		LEFT JOIN member_info mi ON ic.category_no = mi.category_no AND mi.member_no = #{memberNo}
		LEFT JOIN want_info wi ON ic.category_no = wi.category_no AND wi.member_no = #{memberNo}
	WHERE mi.member_no IS NOT NULL OR wi.member_no IS NOT NULL

select mat_date
from matching_date
where mat_no = #{matNo}

mapper.update:
update care_review
set review_status = 'N'
where review_no = #{reviewNo}
