mapper.select:
select *
from hospital
where hospital_name = #{hospitalName} and hospital_address = #{hospitalAddress}

select pt_no
from patient
where member_no = #{memberNo}


select mat_date
from matching_date
where mat_no = #{matNo}

select count(*)
from mat_pt_info
where mat_no = #{matNo}

select mat_no
from mat_pt_info
where pt_no = #{ptNo}

mapper.update:
update care_review
set review_status = 'N'
where review_no = #{reviewNo}
